    private void CalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalcularActionPerformed
        double A, B, C, raizEcuacionCuadratica;
        String x1, x2;
        A=Double.parseDouble(txtA.getText());
        B=Double.parseDouble(txtB.getText());
        C=Double.parseDouble(txtC.getText());
        raizEcuacionCuadratica = Math.pow(B, 2) - (4 * A * C);
        if (raizEcuacionCuadratica < 0) {
            Resultado.setText("Tiene soluciones complejas");
        }
        else if (raizEcuacionCuadratica == 0) {
            Resultado.setText("La soluciÃ³n es: " +
                    "X= " + (-B + Math.sqrt(raizEcuacionCuadratica)) / (2 * A));
        }
        else {
            x1 = String.format("%.02f",(-B + Math.sqrt(raizEcuacionCuadratica)) / (2 * A));
            x2 = String.format("%.02f",(-B - Math.sqrt(raizEcuacionCuadratica)) / (2 * A));
            Resultado.setText("Las dos posibles soluciones " +
                    "son: X1= " + x1 + " y X2= " + x2);
        }
    }//GEN-LAST:event_CalcularActionPerformed

